mapper.update:
UPDATE Board
SET Board_id = #{BoardId}, Board_title = #{BoardTitle}, Board_date =#{BoardDate}, Board_content = #{BoardContent}, Board_score=#{BoardScore},
Board_reference = #{BoardReference}, member_id =#{memberId}, movie_id=#{movieId}
WHERE Board_id = #{BoardId}

mapper.select:

select board_id, board_title, member_id, board_date, board_reference, board_score
from(
	select rownum rnum, board_id, board_title, member_id, board_date, board_reference, board_score
	from(
		select board_id, board_title, member_id, board_date, board_reference, board_score from board order by board_id
	)
	where rownum <= ${endItemNum}
)
where rnum >= ${beginItemNum}
